import React, { Component } from 'react';
import { Container, Row, Col } from 'react-grid-system';
import { Link } from 'react-router-dom';

// Icons
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faStar as bookmarkIcon, faExternalLinkAlt as appIcon, faMapMarkerAlt as locationIcon } from '@fortawesome/free-solid-svg-icons'
import { faClock as deadlineIcon } from '@fortawesome/free-regular-svg-icons'

var bookmarked = false;
var bookmarkButtonClass = "bookmark button-secondary";

if (bookmarked) {
  bookmarkButtonClass = "button-secondary bookmarked";
}

/* ------------------------- */

var userAuth = true;
var greeting = "Your";

if (userAuth) {

} else {
  greeting = "Somebody's";
}

var profileImage = "";
var studentName = "Siraj Chokshi"
var studentPitch = "Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua."

export default class Profile extends Component {
  state = {
  }

  addDefaultSrc(ev) {
    ev.target.src = '/img/usr/missing.png';
  }

  render () {
      return (
        <>
          <div className="hero">
            <div className="hero-inner">
              <h1>{greeting} Profile</h1>
            </div>
          </div>
          <br></br>
          <Container id="user-profile">
            <Row>
              <Col xs={3}>
                <img id="user-profile-image" src={profileImage} onError={this.addDefaultSrc} ></img>
              </Col>
              <Col xs={9} className="user-profile-header">
                <h2>{studentName}</h2>
                <p>{studentPitch}</p>
              </Col>
            </Row>
          </Container>
        </>
      )
   }
}
