import React, { Component } from 'react';
import { Container, Row, Col } from 'react-grid-system';
import { Link } from 'react-router-dom';

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSignInAlt as login } from '@fortawesome/free-solid-svg-icons'

var userAuth = false;

export default class LogIn extends Component {
  state = {
  }

  render () {
    if (userAuth) {
        // BOUNCE-BACK ON AUTH
        return (
          <>
            <h1>LOGGED IN</h1>
          </>
        )
      } else {
      // LOG-IN FIELD
      return (
        <>
          <Container className="login-portal">
            <h1>Login</h1>
            <label className="text-input-label" htmlFor="user-email">Email:</label>
            <input id="user-email" className="input-text" type="email" pattern="/^[a-zA-Z0-9.!#$%&'*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$/" required ></input>
            <label className="text-input-label" htmlFor="user-pass">Password:</label>
            <input id="user-pass" className="input-text"></input>
            <div id="remember-me-wrapper">
              <input type="checkbox" id="remember-me"></input><label htmlFor="remember-me">Keep me logged in</label>
            </div>
            <button><FontAwesomeIcon icon={login} />&nbsp; Login</button>
            <hr></hr>
            <p id="signup-cta">Don't have an account? <Link to="">Click here to sign-up.</Link></p>
          </Container>
        </>
      )
    }
   }
}
