import React, { Component } from 'react';
import { Link } from 'react-router-dom';

// Icons
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faBookmark as bookmark, faExternalLinkAlt as apply, faMapMarkerAlt as locationIcon } from '@fortawesome/free-solid-svg-icons'

var logo = "https://via.placeholder.com/200x200";
var bookmarked = false;
var bookmarkButtonClass = "bookmark button-secondary";
var appLink = "/";
var commitment = "Full time"
var location = "Champaign IL"

export default class Listing extends Component {
  state = {
  }

  render () {
      return (
        <div className="listing">
          <div className="image-wrapper"><img src={logo}></img></div>
          <div className="inf">
            <h2>{company} | {jobName}</h2>
            <p className="sub-title">
              <FontAwesomeIcon icon={locationIcon}></FontAwesomeIcon>
               &nbsp; {location}
            </p>
            <p>Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book.</p>
            <div className="button-wrapper">
              <a href={appLink} className="button">
                Apply &nbsp;
                <FontAwesomeIcon icon={apply}></FontAwesomeIcon>
              </a>
              <button className={bookmarkButtonClass}>
                <FontAwesomeIcon icon={bookmark}></FontAwesomeIcon>
                &nbsp; Bookmark
              </button>
              <a href="" className="similar-pos">Similar positions...</a>
            </div>
          </div>
          <div style={{clear: "both"}}></div>
          <div className="shadow"></div>
        </div>
      )
   }
}
