import React, { Component } from 'react';
import { Link } from 'react-router-dom';

// Icons
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faBookmark as bookmarkIcon, faExternalLinkAlt as appIcon, faMapMarkerAlt as locationIcon } from '@fortawesome/free-solid-svg-icons'
import { faClock as deadlineIcon } from '@fortawesome/free-regular-svg-icons'

export default class Listing extends Component {
  state = {
  }

  addDefaultSrc(ev) {
    ev.target.src = 'https://via.placeholder.com/300';
  }

  render () {

    var jobid, logo, bookmarked, appLink, jobName, jobPage, deadline, location, company, shortDesc;

    function loadIn(x) {
      var sample = x;
      jobid = sample.id;
      logo = "https://via.placeholder.com/256";
      bookmarked = false;
      appLink = "/";
      jobName = sample.listName;
      jobPage = "/";
      deadline = "November 2nd";
      location = sample.listLocation;
      company = sample.listOrgID;
      shortDesc = sample.listDesc;
    }

    var bookmarkButtonClass = "bookmark button-secondary";
    if (bookmarked) {
      bookmarkButtonClass = "button-secondary bookmarked";
    }


      return (
        <div className="listing">
          <script>loadIn(this.props.listObj)</script>
          <Link className="image-wrapper" to={appLink}><img onError={this.addDefaultSrc} src={logo} alt={company}></img></Link>
          <div className="inf">
            <Link to={jobPage}><h2>{jobName} | {company}</h2></Link>
            <p className="sub-title">
              <FontAwesomeIcon icon={locationIcon}></FontAwesomeIcon>
              &nbsp; {location} &nbsp; &mdash; &nbsp;&nbsp;
              <FontAwesomeIcon icon={deadlineIcon}></FontAwesomeIcon>
              &nbsp; {deadline}
            </p>
            <p>
              {shortDesc}
              &nbsp;<Link to={jobPage} className="read-more">Read more...</Link>
            </p>
            <div className="button-wrapper">
              <Link to={jobPage} className="button">
                Apply &nbsp;
                <FontAwesomeIcon icon={appIcon}></FontAwesomeIcon>
              </Link>
              <button className={bookmarkButtonClass}>
                <FontAwesomeIcon icon={bookmarkIcon}></FontAwesomeIcon>
                &nbsp; Bookmark
              </button>
              <a href="" className="similar-pos">Similar positions...</a>
            </div>
          </div>
          <div style={{clear: "both"}}></div>
          <div className="shadow"></div>
        </div>
      )
   }
}
